def solution(stdin: str) -> str:
    n, k = map(int, stdin.split())
    s = stdin.split('\n')[1]

    def is_subsequence(t: str, s: str) -> bool:
        t_index = 0
        for char in s:
            if t_index < len(t) and char == t[t_index]:
                t_index += 1
        return t_index == len(t)

    def generate_subsequences(s: str, k: int) -> list[str]:
        subsequences = set()
        for i in range(len(s) - k + 1):
            subsequence = s[i:i+k]
            if is_subsequence(subsequence, s):
                subsequences.add(subsequence)
        return list(subsequences)

    def min_cost(s: str, k: int) -> int:
        subsequences = generate_subsequences(s, k)
        if len(subsequences) < k:
            return -1

        costs = []
        for subsequence in subsequences:
            costs.append(len(s) - len(subsequence))

        return min(costs)

    return str(min_cost(s, k))

def solution(stdin: str) -> str:
    n, m, D = list(map(int, stdin.split()[:3]))
    edges = [tuple(map(int, line.split())) for line in stdin.split('\n')[1:1+m]]

    # Create adjacency matrix
    adj_matrix = [[0]*n for _ in range(n)]
    for edge in edges:
        adj_matrix[edge[0]-1][edge[1]-1] = 1
        adj_matrix[edge[1]-1][edge[0]-1] = 1

    # Depth-first search to find spanning tree with degree of first vertex = D
    visited = [False]*n
    visited[0] = True
    stack = [0]
    tree_edges = []
    while stack:
        vertex = stack.pop()
        for neighbor in range(n):
            if not visited[neighbor] and adj_matrix[vertex][neighbor] == 1:
                stack.append(neighbor)
                visited[neighbor] = True
                tree_edges.append((vertex+1, neighbor+1))

    # Check if degree of first vertex is equal to D
    if adj_matrix[0].count(1) == D:
        return "YES\n" + '\n'.join(f"{u} {v}" for u, v in tree_edges)
    else:
        return "NO"

