    segments = []
    max_length = 0

    for i in range(n):
        l, r = map(int, stdin.readline().split())
        segments.append((l, r))

    for i in range(n):
        remaining_segments = segments[:i] + segments[i+1:]
        current_length = 0

        for j in range(len(remaining_segments)):
            for k in range(j + 1, len(remaining_segments)):
                l1, r1 = remaining_segments[j]
                l2, r2 = remaining_segments[k]

                if l1 <= l2 <= r1 and l1 <= r2 <= r1:
                    current_length = max(current_length, r2 - l2)
                elif l2 <= l1 <= r2 and l2 <= r1 <= r2:
                    current_length = max(current_length, r1 - l1)
                elif l1 <= l2 <= r1 and l2 <= r1 <= r2:
                    current_length = max(current_length, r1 - l2)
                elif l2 <= l1 <= r2 and l1 <= r2 <= r1:
                    current_length = max(current_length, r2 - l1)

        max_length = max(max_length, current_length)

    return str(max_length)

