def solution(stdin: str) -> str:
    a, b = map(int, stdin.split())

    # If a is even, we can use the same number for both red and blue tiles
    if a % 2 == 0:
        red_tiles = blue_tiles = a // 2
        perimeter = 2 * (red_tiles + blue_tiles) + 2 * red_tiles

    # If b is even, we can use the same number for both red and blue tiles
    elif b % 2 == 0:
        red_tiles = blue_tiles = b // 2
        perimeter = 2 * (red_tiles + blue_tiles) + 2 * blue_tiles

    # If both a and b are odd, we can divide the larger number by 2 and use that number for one color and the remaining tiles for the other color
    else:
        if a > b:
            red_tiles = a // 2
            blue_tiles = a - red_tiles
        else:
            blue_tiles = b // 2
            red_tiles = b - blue_tiles

        perimeter = 2 * (red_tiles + blue_tiles) + 2 * red_tiles

    return str(perimeter)

