def solution(stdin: str) -> str:
    n = int(stdin.split("\n")[0])
    nums = stdin.split("\n")[1].split()
    if len(nums) != n**2:
        return "NO"

    nums = [int(num) for num in nums]
    matrix = [[0] * n for _ in range(n)]

    def is_palindrome(matrix):
        for i in range(n):
            for j in range(n):
                if matrix[i][j] != matrix[i][-j - 1]:
                    return False
                if matrix[-i - 1][j] != matrix[-i - 1][-j - 1]:
                    return False
        return True

    def place_nums(nums, matrix, row=0, col=0):
        if not nums:
            return is_palindrome(matrix)

        for i in range(n):
            if col == n:
                col = 0
                row += 1
            if matrix[row][col] == 0:
                matrix[row][col] = nums.pop(0)
                if place_nums(nums, matrix, row, col + 1):
                    return True
                matrix[row][col] = 0
                nums.append(matrix[row][col])
        return False

    if place_nums(nums, matrix):
        return "YES" + "\n" + "\n".join(" ".join(str(cell) for cell in row) for row in matrix)
    else:
        return "NO"

