        a, b = map(int, stdin.split()[2*i:2*(i+1)])
        projects.append((a, b))

    def is_valid(order):
        rating = r
        for i in order:
            if rating >= projects[i][0]:
                rating += projects[i][1]
            else:
                return False
        return rating >= 0

    valid_orders = []
    for i in range(2**n):
        order = []
        for j in range(n):
            if i & (1 << j):
                order.append(j)
        if is_valid(order):
            valid_orders.append(order)

    return "YES" if valid_orders else "NO"

