def solution(stdin: str) -> str:
    lines = stdin.split("\n")
    n, m, k = map(int, lines[0].split())
    roads = []
    for line in lines[1:m+1]:
        a, b = map(int, line.split())
        roads.append((a, b))

    def dfs(start: int, visited: set) -> set:
        visited.add(start)
        for road in roads:
            if start in road:
                neighbor = road[0] if road[0] != start else road[1]
                if neighbor not in visited:
                    dfs(neighbor, visited)
        return visited

    def count_distance(city: int) -> int:
        visited = dfs(city, set())
        return len(visited) - 1

    def choose_roads(roads_count: int, dist: int) -> List[List[int]]:
        if roads_count == 0:
            return [[]]
        result = []
        for i in range(len(roads)):
            if dist[i] < roads_count:
                continue
            for other_roads in choose_roads(roads_count - 1, [dist[j] - 1 if j != i else dist[j] for j in range(len(roads))]):
                result.append([1] + other_roads)
        return result

    distances = [count_distance(i) for i in range(1, n+1)]
    min_distance = sum(distances)
    min_distances = [distances]
    for i in range(1, n):
        new_distances = [distances[j] + 1 if j != i else distances[j] for j in range(n)]
        if sum(new_distances) < min_distance:
            min_distance = sum(new_distances)
            min_distances = [new_distances]
        elif sum(new_distances) == min_distance:
            min_distances.append(new_distances)

    result = []
    for min_distance in min_distances:
        result += choose_roads(n-1, min_distance)

    return str(min(k, len(result))) + "\n" + "\n".join(["".join(map(str, road)) for road in result[:k]])

