        for i in range(n):
            for j in range(m):
                if i == 0 and j == 0:
                    continue
                if i > 0:
                    dp[i][j] += dp[i - 1][j]
                if j > 0:
                    dp[i][j] += dp[i][j - 1]

        for path in product(range(n), range(m)):
            if xor_sum(grid[i][j] for i, j in zip(path, path[1:])) == k:
                return dp[n - 1][m - 1]
        return 0

