        if num < 2:
            return False
        for i in range(2, int(num**0.5) + 1):
            if num % i == 0:
                return False
        return True

    def gcd(a: int, b: int) -> int:
        if b == 0:
            return a
        return gcd(b, a % b)

    def find_prime_factors(num: int) -> List[int]:
        factors = []
        for i in range(2, int(num**0.5) + 1):
            if num % i == 0 and is_prime(i):
                factors.append(i)
        return factors

    def find_coprime_factors(num: int) -> List[int]:
        factors = []
        for i in range(2, num):
            if num % i == 0 and gcd(num, i) == 1:
                factors.append(i)
        return factors

