    b_iter = iter(b)
    for i in range(n):
        a_i = next(b_iter)
        if is_prime(a_i):
            prime_factors = find_prime_factors(a_i)
            a.extend(prime_factors)
        else:
            coprime_factors = find_coprime_factors(a_i)
            a.extend(coprime_factors)

    return ' '.join(map(str, a[:n]))

