                i += 1
                k += 1

            while j < len(right_half):
                arr[k] = right_half[j]
                j += 1
                k += 1

        return arr

    def count_equal_elements(arr):
        sorted_arr = merge_sort(arr)
        count = 1
        for i in range(1, len(sorted_arr)):
            if sorted_arr[i] == sorted_arr[i - 1]:
