        for i in range(x - size, x + size + 1):
            for j in range(y - size, y + size + 1):
                if i < 0 or i >= n or j < 0 or j >= m:
                    continue
                if grid[i][j] == '*':
                    return False
        return True

    def find_star_possibilities(grid: list) -> list:
        possible_stars = []
        for i in range(n):
            for j in range(m):
                if grid[i][j] == '*':
                    for size in range(1, min(n - i, m - j) + 1):
                        if is_star_possible(size, i, j, grid):
                            possible_stars.append((i, j, size))
        return possible_stars

    possible_stars = find_star_possibilities(grid)
    if not possible_stars:
        return "-1"

    min_stars = len(grid)
    best_stars = []
    for star in possible_stars:
        new_grid = [list(row) for row in grid]
        x, y, size = star
        for i in range(x - size, x + size + 1):
            for j in range(y - size, y + size + 1):
                if i < 0 or i >= n or j < 0 or j >= m:
                    continue
                if grid[i][j] == '*':
                    continue
                new_grid[i][j] = '*'
        new_stars = find_star_possibilities(new_grid)
        if not new_stars or len(new_stars) < min_stars:
            min_stars = len(new_stars)
            best_stars = [star]
        elif len(new_stars) == min_stars:
            best_stars.append(star)

    result = [str(len(best_stars))]
    for star in best_stars:
        result.append(" ".join(map(str, star)))
