    def merge_sort(arr: List[int]) -> List[int]:
        if len(arr) > 1:
            mid = len(arr) // 2
            left = arr[:mid]
            right = arr[mid:]

            merge_sort(left)
            merge_sort(right)

            i = j = k = 0

            while i < len(left) and j < len(right):
                if left[i] < right[j]:
                    arr[k] = left[i]
                    i += 1
                else:
                    arr[k] = right[j]
                    j += 1
                k += 1

            while i < len(left):
                arr[k] = left[i]
                i += 1
                k += 1

            while j < len(right):
                arr[k] = right[j]
                j += 1
                k += 1

        return arr

    def get_rank(arr: List[int]) -> int:
        count = 0
        for i in range(len(arr)):
            for j in range(i + 1, len(arr)):
                if arr[i] > arr[j]:
                    count += 1
        return count + 1

    P_sorted = merge_sort(P)
    Q_sorted = merge_sort(Q)

    P_rank = get_rank(P_sorted)
    Q_rank = get_rank(Q_sorted)

    return str(abs(P_rank - Q_rank))

