def solution(stdin: str) -> str:
    # Parse the input
    n, k = map(int, stdin.split()[:2])
    roads = [tuple(map(int, line.split())) for line in stdin.split()[2:]]

    # Create a graph
    graph = {i: set() for i in range(1, n + 1)}
    for x, y in roads:
        graph[x].add(y)
        graph[y].add(x)

    # Function to check if a city has more than one road belonging to the same company
    def has_multiple_roads(graph: dict, company: int, visited: set) -> bool:
        for city in visited:
            if any(neighbor in visited and graph[city][neighbor] == company for neighbor in graph[city]):
                return True
        return False

    # Brute-force solution to find the minimum number of companies
    min_r = 1
    while True:
        companies = {i: set() for i in range(1, min_r + 1)}
        for x, y in roads:
            for company in range(1, min_r + 1):
                if x not in companies[company] and y not in companies[company]:
                    companies[company].add(x)
                    companies[company].add(y)
                    break
            else:
                min_r += 1
                break
        else:
            break

    # Check if the minimum number of companies satisfies the conditions
    good_cities = 0
    for city in graph:
        if has_multiple_roads(graph, 1, graph[city]):
            good_cities += 1
    if good_cities > k:
        return "Impossible"

    # Print the result
    result = []
    for company in range(1, min_r + 1):
        result.append("".join(str(x) if x in companies[company] else " " for x in range(1, n + 1)))
    return "\n".join(result)

