    n = int(lines[0])
    kids_info = []
    for line in lines[1:]:
        kids_info.append(tuple(map(int, line.split())))

    def find_circle(kids_info: List[Tuple[int, int]]) -> List[int]:
        def dfs(node: int, path: Set[int]) -> bool:
            if node in path:
                return False
            path.add(node)
            for neighbor in [a[0] for a in kids_info if a[1] == node]:
                if not dfs(neighbor, path):
                    return False
            return True

        kids = set(range(1, n + 1))
        for info in kids_info:
            if not dfs(info[0], set()):
                return []
            kids.remove(info[0])
        return list(kids)

    circle = find_circle(kids_info)
    if not circle:
        return "Impossible"
    return " ".join(map(str, circle))

