def solution(stdin: str) -> str:
    n, m = map(int, stdin.split()[0:2])
    a = list(map(int, stdin.split()[2:]))
    special_offers = []
    for _ in range(m):
        special_offers.append(list(map(int, stdin.split()[2 + m * _:2 + m * (_ + 1)])))

    # Dijkstra's algorithm
    graph = [[float('inf')] * n for _ in range(n)]
    for i in range(n):
        graph[i][i] = 0
    for x, y, w in special_offers:
        graph[x - 1][y - 1] = w
    for k in range(n):
        for i in range(n):
            for j in range(n):
                graph[i][j] = min(graph[i][j], graph[i][k] + graph[k][j])

    # Check if the graph is connected
    def is_connected(graph):
        visited = [False] * n
        visited[0] = True
        queue = [0]
        while queue:
            x = queue.pop(0)
            for y in range(n):
                if graph[x][y] != float('inf') and not visited[y]:
                    visited[y] = True
                    queue.append(y)
        return all(visited)

    if is_connected(graph):
        return str(sum(a))
    else:
        return str(sum(a) + min(x[2] for x in special_offers))

