    def find(parent, x):
        if parent[x] != x:
            parent[x] = find(parent, parent[x])
        return parent[x]

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)
        if xroot == yroot:
            return False
        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1
        return True

    def kruskal(n, edges):
        parent = [i for i in range(n)]
        rank = [0] * n
        edges = sorted(edges, key=lambda x: x[2])
        mst = []
        for u, v, w in edges:
            if union(parent, rank, u - 1, v - 1):
                mst.append((u, v, w))
            if len(mst) == n - 1:
                break
        return mst

    n, m = map(int, stdin.splitlines()[0].split())
    edges = [list(map(int, line.split())) for line in stdin.splitlines()[1:]]
    initial_mst = kruskal(n, edges)
    initial_cost = sum(edge[2] for edge in initial_mst)

    operations = 0
    mst = initial_mst
    for i in range(len(edges)):
        modified_edges = [edge for j, edge in enumerate(edges) if j != i]
        mst = kruskal(n, modified_edges)
        if len(mst) != len(initial_mst) or sum(edge[2] for edge in mst) != initial_cost:
            operations += 1
        else:
            break

    return str(operations)

