def solution(stdin: str) -> str:
    n, m = map(int, stdin.splitlines()[0].split())
    edges = [tuple(map(int, line.split())) for line in stdin.splitlines()[1:]]

    # Create adjacency list representation of the graph
    adj_list = [[] for _ in range(n)]
    for v, u in edges:
        adj_list[v-1].append(u-1)
        adj_list[u-1].append(v-1)

    # Initialize parent and rank for union-find
    parent = list(range(n))
    rank = [0] * n

    def find(v):
        if v != parent[v]:
            parent[v] = find(parent[v])
        return parent[v]

    def union(v1, v2):
        root1, root2 = find(v1), find(v2)
        if rank[root1] < rank[root2]:
            parent[root1] = root2
        elif rank[root1] > rank[root2]:
            parent[root2] = root1
        else:
            parent[root2] = root1
            rank[root1] += 1

    # Find MST using Kruskal's algorithm
    mst = []
    edges = sorted(edges, key=lambda x: x[2])
    for v, u, _ in edges:
        if find(v) != find(u):
            union(v, u)
            mst.append((v, u))

    # Find spanning tree with maximum degree
    max_degree = 0
    max_degree_tree = []
    for v in range(n):
        degree = len([u for u in adj_list[v] if u in [x-1 for x, y in mst] and u != v])
        if degree > max_degree:
            max_degree = degree
            max_degree_tree = [(v+1, u+1) for u in adj_list[v] if u in [x-1 for x, y in mst] and u != v]

    return '\n'.join(f'{v} {u}' for v, u in max_degree_tree)

