    def floyd_warshall(adj_list: List[List[Tuple[int, int]]]) -> List[List[int]]:
        n = len(adj_list)
        dist = [[float('inf')] * n for _ in range(n)]
        for i in range(n):
            dist[i][i] = 0
        for u in range(n):
            for v, w in adj_list[u]:
                dist[u][v] = w
        for k in range(n):
            for i in range(n):
                for j in range(n):
                    dist[i][j] = min(dist[i][j], dist[i][k] + dist[k][j])
        return dist

    def kth_smallest(dist: List[List[int]], k: int) -> int:
        n = len(dist)
        arr = sorted([dist[i][j] for i in range(n) for j in range(n) if i < j])
        return arr[k - 1]

    stdin = read_input()
    n, adj_list, k = stdin
    dist = floyd_warshall(adj_list)
    return str(kth_smallest(dist, k))

