    grid = [list(line) for line in stdin[1:]]

    def neighbours(i, j):
        for di, dj in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            ni, nj = i + di, j + dj
            if 0 <= ni < H and 0 <= nj < W:
                yield ni, nj

    def dfs(i, j):
        if grid[i][j] == "#":
            return True
        grid[i][j] = "#"
        for ni, nj in neighbours(i, j):
            if dfs(ni, nj):
                return True
        grid[i][j] = "."
        return False

    for i in range(H):
        for j in range(W):
            if grid[i][j] == "#":
                if dfs(i, j):
                    continue
                return "No"
    return "Yes"

