    A, B, C, X = map(int, stdin.split())

    if X < 50 or X > 20000 or X % 50 != 0:
        raise ValueError("Invalid X value")

    def count_ways(A, B, C, target, memo):
        if target == 0:
            return 1
        if target < 0:
            return 0
        if A <= 0 and B <= 0 and C <= 0:
            return 0

        key = (A, B, C, target)
        if key in memo:
            return memo[key]

        count = 0
        if A > 0:
            count += count_ways(A - 1, B, C, target - 500, memo)
        if B > 0:
            count += count_ways(A, B - 1, C, target - 100, memo)
        if C > 0:
            count += count_ways(A, B, C - 1, target - 50, memo)

        memo[key] = count
        return count

    return str(count_ways(A, B, C, X, {}))

