    def find_min_cost(mst: List[List[int]]) -> int:
        n = len(mst)
        inf = float('inf')
        cost = [[inf] * n for _ in range(n)]
        for i in range(n):
            cost[i][i] = 0
        for u, v, w in mst:
            cost[u - 1][v - 1] = w
        for k in range(n):
            for i in range(n):
                for j in range(n):
                    cost[i][j] = min(cost[i][j], cost[i][k] + cost[k][j])
        return sum(min(row) for row in cost)

    def find_mst(n: int, roads: List[List[int]]) -> List[List[int]]:
        graph = [[0] * n for _ in range(n)]
        for u, v, w in roads:
            graph[u - 1][v - 1] = w
        for i in range(n):
            graph[i][i] = 0
        mst = []
        visited = [False] * n
        visited[0] = True
        while len(mst) < n - 1:
            min_edge = (0, inf)
            for i in range(n):
                if not visited[i]:
                    for j in range(n):
                        if graph[i][j] < min_edge[1] and i != j:
                            min_edge = (i, graph[i][j])
            u, v = min_edge
            mst.append((u + 1, v + 1, graph[u][v]))
            visited[v] = True
        return mst

    lines = stdin.split('\n')
    n, m, k = map(int, lines[0].split())
    roads = [list(map(int, lines[i].split())) for i in range(1, m + 1)]
