        for i in range(4):
            for j in range(4):
                if grid[i][j] == 0:
                    for k in range(i, 4):
                        if grid[k][j] != 0:
                            grid[i][j], grid[k][j] = grid[k][j], grid[i][j]
                            break

    def move_left(grid):
        for i in range(4):
            for j in range(1, 4):
                if grid[i][j] == grid[i][j - 1]:
                    grid[i][j] *= 2
                    grid[i][j - 1] = 0

    def move_right(grid):
        for i in range(4):
            for j in range(2, -1, -1):
                if grid[i][j] == grid[i][j + 1]:
                    grid[i][j] *= 2
                    grid[i][j + 1] = 0

    def move_up(grid):
        for j in range(4):
            for i in range(1, 4):
                if grid[i][j] == grid[i - 1][j]:
                    grid[i][j] *= 2
                    grid[i - 1][j] = 0

    def move_down(grid):
        for j in range(4):
            for i in range(2, -1, -1):
                if grid[i][j] == grid[i + 1][j]:
                    grid[i][j] *= 2
                    grid[i + 1][j] = 0

    if move == 0:
        pass
    elif move == 1:
        move_left(grid)
    elif move == 2:
        move_up(grid)
    elif move == 3:
        move_right(grid)

    merge(grid)
    move_left(grid)
    move_up(grid)

    output = ""
