    import re
    import sys
    sys.setrecursionlimit(10**9)
    from typing import List, Optional

    class ListNode:
        def __init__(self, val=0, next=None):
            self.val = val
            self.next = next

    def build_linked_list(s: str) -> Optional[ListNode]:
        s = re.sub('\s+', '', s).strip()[1:-1]
        nodes = [ListNode(int(i)) for i in s.split(',')]
        for i in range(1, len(nodes)):
            nodes[i-1].next = nodes[i]
        return nodes[0] if nodes else None

    def has_cycle(head: ListNode) -> bool:
        slow, fast = head, head
        while fast and fast.next:
            slow = slow.next
            fast = fast.next.next
            if slow == fast:
                return True
        return False

    head = build_linked_list(stdin)
    return 'true' if has_cycle(head) else 'false'

