    # Create a list of lists to represent the crossword
    crossword = []
    for i in range(R):
        row = list(stdin.split('\n')[i+1])
        crossword.append(row)

    # Find the lexicographically smallest word in the crossword
    def get_word(start_x, start_y, direction):
        word = ""
        x, y = start_x, start_y
        while crossword[x][y] != "#" and crossword[x][y] != " ":
            word += crossword[x][y]
            if direction == "down":
                x += 1
            else:
                y += 1
        return word

    def get_lexicographically_smallest_word():
        for i in range(R):
            for j in range(C):
                if crossword[i][j] != "#" and crossword[i][j] != " ":
                    # Check downwards
                    down_word = get_word(i, j, "down")
                    if len(down_word) >= 2:
                        return down_word
                    # Check rightwards
                    right_word = get_word(i, j, "right")
                    if len(right_word) >= 2:
                        return right_word
        return ""

    return get_lexicographically_smallest_word()

