    def drop_piece(piece: int) -> int:
        if piece == 1:
            return 1
        elif piece == 2:
            return 2
        elif piece == 3:
            return 4
        elif piece == 4:
            return 8
        elif piece == 5:
            return 16
        elif piece == 6:
            return 32
        elif piece == 7:
            return 64
        else:
            return 0

    # Function to calculate the number of ways to drop the piece on the field
    def ways_to_drop(heights: List[int]) -> int:
        ways = 0
        for i in range(len(heights)):
            ways += drop_piece(heights[i])
        return ways

    return str(ways_to_drop(heights))

