        'w': '9', 'x': '99', 'y': '999', 'z': '9999'
    }

    # create dictionary of words that can be constructed from the key presses
    constructable_words = {}
    for word in words:
        constructable_words[word] = sum(len(key_presses[char]) for char in word) == len(word) * len(key_presses[word[0]])

