def solution(stdin: str) -> str:
    t, N, M = map(int, stdin.split()[0:3])
    grid = [list(stdin.split('\n')[i]) for i in range(1, N+1)]

    def is_burning(row: int, col: int) -> bool:
        return grid[row][col] == '1'

    def is_safe(row: int, col: int) -> bool:
        return grid[row][col] == '0'

    def is_up_safe(row: int, col: int) -> bool:
        return grid[row][col] != 'U' or (row - 1 >= 0 and is_safe(row - 1, col))

    def is_down_safe(row: int, col: int) -> bool:
        return grid[row][col] != 'D' or (row + 1 < N and is_safe(row + 1, col))

    def is_left_safe(row: int, col: int) -> bool:
        return grid[row][col] != 'L' or (col - 1 >= 0 and is_safe(row, col - 1))

    def is_right_safe(row: int, col: int) -> bool:
        return grid[row][col] != 'R' or (col + 1 < M and is_safe(row, col + 1))

    def is_exit(row: int, col: int) -> bool:
        return col == 0 or col == M - 1 or row == 0 or row == N - 1

    def dfs(row: int, col: int, time: int) -> int:
        if is_burning(row, col) or time > t:
            return float('inf')
        if is_exit(row, col):
            return 0

        min_distance = float('inf')
        if is_up_safe(row, col):
            min_distance = min(min_distance, dfs(row - 1, col, time + 1))
        if is_down_safe(row, col):
            min_distance = min(min_distance, dfs(row + 1, col, time + 1))
        if is_left_safe(row, col):
            min_distance = min(min_distance, dfs(row, col - 1, time + 1))
        if is_right_safe(row, col):
            min_distance = min(min_distance, dfs(row, col + 1, time + 1))

        return min_distance + 1

    start_row, start_col = -1, -1
    for i in range(N):
        for j in range(M):
            if grid[i][j] == 'S':
                start_row, start_col = i, j
                break
        if start_row != -1 and start_col != -1:
            break

    min_distance = dfs(start_row, start_col, 1)
    return "NOT POSSIBLE" if min_distance == float('inf') else str(min_distance)

