        best_probability = 0.0
        for i in range(fridge_slots):
            # Find the maximum number of new bottles that can be added to slot i
            max_bottles_i = min(fridge_capacity - curr_bottles[i], new_bottles)
            # Calculate the probability of getting a cold soda with the current refill scheme
            new_bottles_i = min(max_bottles_i, next_students)
            old_bottles_i = max_bottles_i - new_bottles_i
            cold_probability = (old_bottles_i + 1) / (fridge_capacity - curr_bottles[i] + 1)
            if cold_probability > best_probability:
                best_scheme = [new_bottles_i] * fridge_slots
                best_probability = cold_probability
            # Update the number of new bottles and the number of students
            new_bottles -= new_bottles_i
            next_students -= new_bottles_i
        # Check if it's possible to get a cold soda for all the next students
        if next_students > 0:
            return "impossible"
        else:
            return " ".join(str(x) for x in best_scheme)

