        for _ in range(n - 1):
            u, v = read_line()
            graph[int(u)].append(int(v))
            graph[int(v)].append(int(u))
        return graph

    def dijkstra(graph: Dict[int, List[int]], start: int, target: int, k: int) -> int:
        dist = {i: float('inf') for i in graph}
        dist[start] = 0
        queue = [(start, 0)]
        while queue:
            u, time = heapq.heappop(queue)
            if time > dist[u]:
                continue
            if u == target:
                return dist[target]
            for v in graph[u]:
                new_time = time + k if u == start and v == target else time + 1
                if new_time < dist[v]:
                    dist[v] = new_time
                    heapq.heappush(queue, (v, new_time))
        return -1

    n, m = read_line()
    a, b, k, g = read_line()
    g_route = read_line()
    streets = {}
    for _ in range(m):
        u, v, l = read_line()
        streets[(u, v)] = streets[(v, u)] = int(l)

    return str(dijkstra(read_graph(n), a, b, streets[tuple(sorted([a, b]))]))

