        for language in characters[char1]["other_languages"] & languages:
            for other_char in char_list:
                if other_char not in visited and language in characters[other_char]["other_languages"]:
                    if can_converse(other_char, char2, visited):
                        return True

        return False

    max_size = 0
    for i in range(n):
        char1 = char_list[i]
        size = 1

        for j in range(n):
            if i == j:
