            if cost > budget:
                return float('inf')
            if week_idx == weeks:
                return cost

            min_cost = float('inf')
            for i in range(hotels):
                if check_availability(cost + prices[i], i, week_idx):
                    min_cost = min(min_cost, backtrack(i, week_idx + 1, cost + prices[i]))

            return min_cost

        return backtrack(0, 0, 0)

