    n, m = map(int, stdin.split()[0].split())
    grid = stdin.split("\n")[1:]
    coffee_shops = []

    # Check for adjacent cells
    def is_adjacent(cell1, cell2):
        x1, y1 = cell1
        x2, y2 = cell2
        return abs(x1 - x2) + abs(y1 - y2) == 1

    # DFS to find adjacent cells
    def dfs(cell, visited, coffee_shops):
        x, y = cell
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < n and 0 <= ny < m and (nx, ny) not in visited and grid[nx][ny] == '.':
                visited.add((nx, ny))
                if is_adjacent(cell, (nx, ny)):
                    coffee_shops[-1].append((nx, ny))
                else:
                    coffee_shops.append([(nx, ny)])
                dfs((nx, ny), visited, coffee_shops)

    # Find the first land cell and start DFS
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '.':
                coffee_shops.append([(i, j)])
                dfs((i, j), set(), coffee_shops)
                break
        else:
            continue
        break

    # Check for adjacent cells and remove shops if necessary
    for i in range(len(coffee_shops)):
        for j in range(len(coffee_shops)):
            if i != j:
                for cell1 in coffee_shops[i]:
                    for cell2 in coffee_shops[j]:
                        if is_adjacent(cell1, cell2):
                            return "No solution"

    # Add coffee shops to the grid
    for i in range(n):
        for j in range(m):
            if (i, j) in coffee_shops[0]:
                grid[i] = grid[i][:j] + 'E' + grid[i][j+1:]

    return '\n'.join(grid)

