def solution(stdin: str) -> str:
    def initialize_table(key_phrase: str) -> list:
        key_phrase = key_phrase.replace(" ", "").replace("q", "")
        table = []
        alphabet = list("abcdefghijklmnopqrstuvwxyz")

        for i in range(5):
            row = []
            for j in range(5):
                if len(key_phrase) == 0:
                    row.append(alphabet.pop(0))
                else:
                    row.append(key_phrase[0])
                    key_phrase = key_phrase[1:]
            table.append(row)

        for i in range(len(alphabet)):
            table[i // 5][i % 5] = alphabet[i]

        return table

    def encrypt(text: str, table: list) -> str:
        encrypted_text = ""
        for i in range(0, len(text), 2):
            pair = text[i : i + 2]
            if len(pair) == 2:
                if pair[0] == pair[1]:
                    pair += "X"
                row1 = table.index(pair[0])
                col1 = table[row1].index(pair[0])
                row2 = table.index(pair[1])
                col2 = table[row2].index(pair[1])

                if row1 == row2:
                    encrypted_text += table[row1][(col1 + 1) % 5] + table[row2][(col2 + 1) % 5]
                elif col1 == col2:
                    encrypted_text += table[(row1 + 1) % 5][col1] + table[(row2 + 1) % 5][col2]
                else:
                    encrypted_text += table[row1][col2] + table[row2][col1]
            else:
                encrypted_text += "X"

        return encrypted_text.upper()

    key_phrase, text = stdin.split("\n")
    table = initialize_table(key_phrase)
    encrypted_text = encrypt(text, table)

    return encrypted_text

