        if x < 0 or x >= N or y < 0 or y >= M:
            return
        if grid[x][y] == "V":
            return
        if grid[x][y] == ".":
            grid[x][y] = "V"
        else:
            if x - 1 >= 0 and grid[x - 1][y] == ".":
                grid[x - 1][y] = "V"
            if y - 1 >= 0 and grid[x][y - 1] == ".":
                grid[x][y - 1] = "V"
            if x + 1 < N and grid[x + 1][y] == ".":
                grid[x + 1][y] = "V"
            if y + 1 < M and grid[x][y + 1] == ".":
                grid[x][y + 1] = "V"

    for i in range(N):
        for j in range(M):
            spread_water(i - 1, j)
            spread_water(i, j - 1)
            spread_water(i, j + 1)
            spread_water(i + 1, j)

    result = ""
    for row in grid:
        result += "".join(row) + "\n"

    return result

