def solution(stdin: str) -> str:
    n, m, k = map(int, stdin.split()[0:3])
    a = [list(map(int, row.split())) for row in stdin.split()[3:]]

    def is_divisible_by_k(sum: int) -> bool:
        return sum % k == 0

    def backtrack(row: int, current_sum: int, remaining_elements: int) -> int:
        if row == n:
            return current_sum if is_divisible_by_k(current_sum) else 0

        choose_elements = backtrack(row + 1, current_sum + remaining_elements, remaining_elements // 2)
        skip_elements = backtrack(row + 1, current_sum, remaining_elements // 2)

        return max(choose_elements, skip_elements)

    max_sum = backtrack(0, 0, m)
    return str(max_sum)

