    n = int(stdin.split('\n')[0])
    a = list(map(int, stdin.split('\n')[1].split()))
    dp = [1] * n
    prev = [None] * n

    for i in range(n):
        for j in range(i):
            if a[i] > a[j] and dp[i] < dp[j] + 1:
                dp[i] = dp[j] + 1
                prev[i] = j

    k = max(dp)
    ans = ['R'] * k
    i = dp.index(k)
    while i is not None:
        if prev[i] is not None and dp[i] == dp[prev[i]] + 1:
            ans[dp[i] - 1] = 'L'
        i = prev[i]

    return f'{k}\n' + ''.join(reversed(ans))

