def solution(stdin: str) -> str:
    def gcd(a, b):
        while b:
            a, b = b, a % b
        return a

    def lcm(a, b):
        return a * b // gcd(a, b)

    t = int(stdin.split('\n')[0])
    output = []
    for i in range(t):
        a, b, c = map(int, stdin.split('\n')[i + 1].split())
        A = a
        B = lcm(a, b)
        C = lcm(B, c)
        res = 0
        if A != a:
            res += abs(A - a) - 1
        if B != b:
            res += abs(B - b) - 1
        if C != c:
            res += abs(C - c) - 1
        output.append(f"{res}\n{A} {B} {C}")
    return "\n".join(output)

