        exam_list.append((s - 1, d, c))

    exam_list.sort(key=lambda x: x[1])

    schedule = find_schedule(exam_list, n, m, 0)

    if not schedule:
        return -1

    for i in range(n):
        if i == 0:
            schedule[i] = m + 1 if exam_list and exam_list[0][0] == i else 0
