    def is_valid_tree(tree, degree):
        degrees = [0] * (n + 1)
        for u, v in tree:
            degrees[u] += 1
            degrees[v] += 1
        return degrees[1] == degree

    def find_spanning_tree(edges, degree):
        for edge in edges:
            tree = [edge]
            if is_valid_tree(tree, degree):
                return tree
        return None

    def find_spanning_tree_with_backtracking(edges, degree):
        def backtrack(tree, used_edges):
            if is_valid_tree(tree, degree):
                return tree
            for i, edge in enumerate(edges):
                if i not in used_edges:
                    tree.append(edge)
                    used_edges.add(i)
                    result = backtrack(tree, used_edges)
                    if result is not None:
                        return result
                    tree.pop()
                    used_edges.remove(i)
            return None

        return backtrack([], set())

    lines = stdin.splitlines()
    n, m, degree = map(int, lines[0].split())
    edges = [tuple(map(int, line.split())) for line in lines[1:]]

    if degree > m or degree > n - 1:
        return "NO"

    tree = find_spanning_tree_with_backtracking(edges, degree)
    if tree is None:
        return "NO"

    return "YES\n" + "\n".join(" ".join(map(str, edge)) for edge in tree)

