    n = int(n)

    MOD = 1000000007
    dp = [0] * (2 * n + 1)
    dp[0] = 1

    for i in range(len(s)):
        new_dp = [0] * (2 * n + 1)
        for j in range(i, len(s), 2):
            if s[i:j + 1] == '()' * (j - i + 1):
                for k in range(j - i + 1, 2 * n + 1):
