    def is_divisible_by_25(num):
        while num >= 25:
            if num % 25 == 0:
                return True
            num //= 10
        return False

    def is_valid_number(num):
        return num >= 1 and num <= 10 ** 18 and num // 10 or num % 10 != 0

    def get_adjacent_numbers(num):
        adjacent_numbers = set()
        str_num = str(num)
        for i in range(len(str_num) - 1):
            adjacent_numbers.add(int(str_num[:i] + str_num[i + 1] + str_num[i] + str_num[i + 2:]))
        return adjacent_numbers

    queue = {n}
    visited = set()
    move_count = 0

    while queue:
        new_queue = set()
        for num in queue:
            if is_divisible_by_25(num):
                return move_count
            for adjacent_num in get_adjacent_numbers(num):
                if adjacent_num not in visited and is_valid_number(adjacent_num):
                    new_queue.add(adjacent_num)
        visited.update(queue)
        queue = new_queue
        move_count += 1

    return -1

