    n = int(stdin.split("\n")[0])
    numbers = list(map(int, stdin.split("\n")[1].split()))

    def is_palindromic(matrix):
        for i in range(n):
            for j in range(n):
                if matrix[i][j] != matrix[n - i - 1][j]:
                    return False
                if matrix[i][j] != matrix[i][n - j - 1]:
                    return False
        return True

    def backtrack(matrix, used):
        if len(used) == n * n:
            if is_palindromic(matrix):
                return "YES"
            return "NO"

        row = len(used) // n
        col = len(used) % n

        for num in numbers:
            if num not in used:
                matrix[row][col] = num
                used.append(num)

                result = backtrack(matrix, used)
                if result != "NO":
                    return result

                used.pop()
                matrix[row][col] = 0

        return "NO"

    matrix = [[0] * n for _ in range(n)]
    result = backtrack(matrix, [])

    if result == "YES":
        return "YES\n" + "\n".join(" ".join(str(matrix[i][j]) for j in range(n)) for i in range(n))
    return result

