    def find_longest_common_substring(a: int, b: int) -> int:
        length = 0
        while a + length < n and b + length < n and is_equal(a + length, b + length):
            length += 1
        return length

    def abbreviate(start: int, end: int) -> str:
        return "".join(word.upper()[0] for word in words[start:end])

