            graph = defaultdict(set)
            for i, road in enumerate(roads):
                if option[i] == "1":
                    graph[road[0]].add(road[1])
                    graph[road[1]].add(road[0])
            return dfs(1, n, set(), graph)

        def next_option(option, index):
            if index == m:
                return
            option[index] = "1"
            if is_valid(option):
                yield "".join(option)
            else:
                yield from next_option(option, index + 1)
            option[index] = "0"
            yield from next_option(option, index + 1)

        options = set()
        for option in next_option(["0"] * m, 0):
            if len(options) == k:
                break
            options.add(option)
        return options

    def parse_input(stdin: str):
        n, m, k = map(int, stdin.split()[:3])
        roads = [tuple(map(int, line.split())) for line in stdin.split("\n")[1:1+m]]
        return n, m, k, roads

    n, m, k, roads = parse_input(stdin)
    options = find_options(n, m, k, roads)
    return str(len(options)) + "\n" + "\n".join(options)

