    def distance(x1, y1, x2, y2):
        return ((x1 - x2) ** 2 + (y1 - y2) ** 2) ** 0.5

    def mean_angle(points):
        angles = []
        for i in range(len(points)):
            x1, y1 = points[i]
            x2, y2 = points[(i + 1) % len(points)]
            angles.append(math.atan2(y2 - y1, x2 - x1))
        return sum(angles) / len(angles)

    def closest_point(origin, points):
        closest_distance = float('inf')
        closest_point = None
        for point in points:
            distance_to_point = distance(*origin, *point)
            if distance_to_point < closest_distance:
                closest_distance = distance_to_point
                closest_point = point
        return closest_point

    def furthest_point(origin, points):
        furthest_distance = float('-inf')
        furthest_point = None
        for point in points:
            distance_to_point = distance(*origin, *point)
            if distance_to_point > furthest_distance:
                furthest_distance = distance_to_point
                furthest_point = point
        return furthest_point

    def solve(points):
        center_of_mass = tuple(sum(coord) / len(points) for coord in zip(*points))
        angle = mean_angle(points)

        closest_to_center = closest_point(center_of_mass, points)
        furthest_from_center = furthest_point(center_of_mass, points)

        radius_to_closest = distance(center_of_mass, closest_to_center)
        radius_to_furthest = distance(center_of_mass, furthest_from_center)

        return angle + radius_to_closest - radius_to_furthest

    n = int(stdin.readline())
    points = []
    for _ in range(n):
        x, y = map(float, stdin.readline().split())
        points.append((x, y))

    return '{:.3f}'.format(solve(points))

