    def kruskal(edges):
        parent = [i for i in range(n)]
        rank = [0] * n
        def find(x):
            if parent[x] != x:
                parent[x] = find(parent[x])
            return parent[x]
        def merge(x, y):
            x, y = find(x), find(y)
            if x == y:
                return False
            if rank[x] > rank[y]:
                parent[y] = x
            else:
                parent[x] = y
                if rank[x] == rank[y]:
                    rank[y] += 1
            return True
        edges.sort(key=lambda x: x[2])
        return sum(cost for cost, x, y in edges if merge(x, y))

    special_offers = [(w, x, y) for x, y, w in offers]
    return str(kruskal(special_offers))

