def solution(stdin: str) -> str:
    def find(parent, i):
        t = parent[i]
        if t < 0:
            return i
        t = find(parent, t)
        parent[i] = t
        return t

    def union(parent, rank, x, y):
        x = find(parent, x)
        y = find(parent, y)

        if x == y:
            return False

        if rank[x] > rank[y]:
            parent[y] = x
        else:
            parent[x] = y
            if rank[x] == rank[y]:
                rank[y] += 1
        return True

    def kruskal(n, m, edges):
        parent, rank = [-1] * n, [0] * n
        mst_cost = 0
        mst_edges = []

        for e in sorted(edges, key=lambda x: x[2]):
            u, v, w = e
            if union(parent, rank, u - 1, v - 1):
                mst_cost += w
                mst_edges.append(e)

        return mst_cost, mst_edges

    lines = stdin.strip().split('\n')
    n, m = map(int, lines[0].split())
    edges = [list(map(int, line.split())) for line in lines[1:]]

    mst_cost, mst_edges = kruskal(n, m, edges)
    for i in range(m):
        edges[i][2] += 1
        new_mst_cost, new_mst_edges = kruskal(n, m, edges)
        if new_mst_cost != mst_cost or new_mst_edges != mst_edges:
            return i
        edges[i][2] -= 1

    return "0"

