            bad_points += 1

    # If there are no bad points, output 0 and exit
    if bad_points == 0:
        return "0\n"

    # Otherwise, find the minimum number of segments to remove
    segments_to_remove = []
    removed_bad_points = 0
    while removed_bad_points < bad_points:
        segment_index, count = max(enumerate(points), key=lambda x: x[1] if x[0] not in segments_to_remove else 0)
