    def kruskal(n, edges):
        parent = list(range(n))
        rank = [0] * n
        result = []

        def find(x):
            if parent[x] != x:
                parent[x] = find(parent[x])
            return parent[x]

        def union(x, y):
            x = find(x)
            y = find(y)
            if x == y:
                return False
            if rank[x] > rank[y]:
                parent[y] = x
            else:
                parent[x] = y
                if rank[x] == rank[y]:
                    rank[y] += 1
            return True

        for v1, v2 in edges:
            if union(v1 - 1, v2 - 1):
                result.append((v1, v2))

        return result

    lines = stdin.split('\n')
    n, m = map(int, lines[0].split())
    edges = [tuple(map(int, line.split())) for line in lines[1:]]
    tree = kruskal(n, edges)
    return '\n'.join(f'{v1} {v2}' for v1, v2 in tree)

