    edges = [list(map(int, line.split())) for line in stdin.split('\n')[1:]]

    # Initialize the shortest path matrix
    d = [[float('inf')] * n for _ in range(n)]
    for i in range(n):
        d[i][i] = 0

    # Floyd-Warshall algorithm
    for i in range(n):
        for j in range(n):
            if i != j:
                d[i][j] = min(d[i][j], edges[i][j])

    for k in range(n):
